from fastapi.testclient import TestClientfrom main import appclient = TestClient(app)def test_home_page_status():    path = "/"    # python request    #  r = requests.get(path)    response = client.get(path)    status_code = response.status_code    content_type = response.headers["Content-Type"]    assert status_code == 200  # HTTP response    assert content_type == "application/json"